package main

import (
	"fmt"
	"strconv"
)

var input string = ""

func main() {
	fmt.Println("D6P1: " + day6P1Answer())
	fmt.Println("D6P2: " + day6P2Answer())
}

func day6P2Answer() string {
	var answer string = ""

	for i, _ := range input {
		if isAllUnique(input[i : i+14]) {
			answer = strconv.Itoa(i + 14)
			break
		}
	}
	return answer
}

func day6P1Answer() string {
	var answer string = ""

	for i := range input {
		if isAllUnique(input[i : i+4]) {
			answer = strconv.Itoa(i + 4)
			break
		}
	}
	return answer
}

func isAllUnique(s string) bool {
	p := []rune(s)
	for i := 0; i < len(p)-1; i++ {
		for j := i + 1; j < len(p); j++ {
			if p[j] == p[i] {
				return false
			}
		}
	}
	return true
}
